        uint256 totalUnderlyingBuffered = getTotalUnderlyingBuffered(
            underlyingToken
        );

        if (totalUnderlyingBuffered < flowAvailable[underlyingToken]) {
            return totalUnderlyingBuffered;
        } else {
            return flowAvailable[underlyingToken];
        }
    }

