contracts/base/SelfPermit.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-IERC20Permit.sol";

import "../interfaces/ISelfPermit.sol";
import "../interfaces/IERC20PermitAllowed.sol";

/// @title  Self Permit
/// @author Uniswap Labs
/// @notice Functionality to call permit on any EIP-2612-compliant token for use in the route
///
/// @dev These functions are expected to be embedded in multicalls to allow EOAs to approve a contract and call a function
/// that requires an approval in a single transaction.
abstract contract SelfPermit is ISelfPermit {
    /// @inheritdoc ISelfPermit
    function selfPermit(
        address token,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public payable override {
        IERC20Permit(token).permit(msg.sender, address(this), value, deadline, v, r, s);
    }

    /// @inheritdoc ISelfPermit
    function selfPermitIfNecessary(
        address token,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable override {
        if (IERC20(token).allowance(msg.sender, address(this)) < value) selfPermit(token, value, deadline, v, r, s);
    }

    /// @inheritdoc ISelfPermit
    function selfPermitAllowed(
        address token,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public payable override {
        IERC20PermitAllowed(token).permit(msg.sender, address(this), nonce, expiry, true, v, r, s);
    }

    /// @inheritdoc ISelfPermit
    function selfPermitAllowedIfNecessary(
        address token,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable override {
        if (IERC20(token).allowance(msg.sender, address(this)) < type(uint256).max)
        selfPermitAllowed(token, nonce, expiry, v, r, s);
    }
}

contracts/interfaces/ISelfPermit.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

/// @title  Self Permit
/// @author Uniswap Labs
///
/// @notice Functionality to call permit on any EIP-2612-compliant token.
interface ISelfPermit {
    /// @notice Permits this contract to spend a given token from `msg.sender`
    ///
    /// @dev The `owner` is always msg.sender and the `spender` is always `address(this)`.
    ///
    /// @param token    The address of the token spent.
    /// @param value    The amount that can be spent of token.
    /// @param deadline A timestamp, the current block timestamp must be less than or equal to this timestamp.
    /// @param v        Must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r        Must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s        Must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function selfPermit(
        address token,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable;

    /// @notice Permits this contract to spend a given token from `msg.sender`.
    ///
    /// @dev The `owner` is always msg.sender and the `spender` is always address(this).
    ///
    /// Can be used instead of #selfPermit to prevent calls from failing due to a frontrun of a call to
    /// [selfPermit](#selfpermit).
    ///
    /// @param token    The address of the token spent.
    /// @param value    The amount that can be spent of token.
    /// @param deadline A timestamp, the current block timestamp must be less than or equal to this timestamp.
    /// @param v        Must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r        Must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s        Must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function selfPermitIfNecessary(
        address token,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable;

    /// @notice Permits this contract to spend the sender's tokens for permit signatures that have the `allowed`
    ///         parameter.
    ///
    /// @dev The `owner` is always msg.sender and the `spender` is always `address(this)`.
    ///
    /// @param token  The address of the token spent.
    /// @param nonce  The current nonce of the owner.
    /// @param expiry The timestamp at which the permit is no longer valid.
    /// @param v      Must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r      Must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s      Must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function selfPermitAllowed(
        address token,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable;

    /// @notice Permits this contract to spend the sender's tokens for permit signatures that have the `allowed`
    ///         parameter.
    ///
    /// @dev The `owner` is always msg.sender and the `spender` is always `address(this)`.
    ///
    /// Can be used instead of [selfPermitAllowed](#selfpermitallowed) to prevent calls from failing due to a frontrun
    /// of a call to [selfPermitAllowed](#selfpermitallowed).
    ///
    /// @param token  The address of the token spent.
    /// @param nonce  The current nonce of the owner.
    /// @param expiry The timestamp at which the permit is no longer valid.
    /// @param v      Must produce valid secp256k1 signature from the holder along with `r` and `s`
    /// @param r      Must produce valid secp256k1 signature from the holder along with `v` and `s`
    /// @param s      Must produce valid secp256k1 signature from the holder along with `r` and `v`
    function selfPermitAllowedIfNecessary(
        address token,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable;
}

contracts/interfaces/IERC20PermitAllowed.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

/// @title  IERC20PermitAllowed
/// @author Uniswap Labs
///
/// @notice Interface used by DAI/CHAI for permit.
interface IERC20PermitAllowed {
    /// @notice Approve the spender to spend some tokens via the holder signature.
    ///
    /// @dev This is the permit interface used by DAI and CHAI.
    ///
    /// @param holder  The address of the token holder, the token owner.
    /// @param spender The address of the token spender.
    /// @param nonce   The holder's nonce, increases at each call to permit.
    /// @param expiry  The timestamp at which the permit is no longer valid.
    /// @param allowed Boolean that sets approval amount, true for type(uint256).max and false for 0.
    /// @param v       Must produce valid secp256k1 signature from the holder along with `r` and `s`.
    /// @param r       Must produce valid secp256k1 signature from the holder along with `v` and `s`.
    /// @param s       Must produce valid secp256k1 signature from the holder along with `r` and `v`.
    function permit(
        address holder,
        address spender,
        uint256 nonce,
        uint256 expiry,
        bool allowed,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;
}

